package main

import (
	_ "embed"
	"errors"
	"flag"
	"html/template"
	"os"
	"path/filepath"

	"github.com/tdewolff/minify/v2"
	"github.com/tdewolff/minify/v2/css"
	"github.com/tdewolff/minify/v2/html"
	"github.com/tdewolff/minify/v2/js"
)

//go:embed page.tmpl
var data string

//go:embed style.css
var styleData []byte

//go:embed script.js
var scriptData []byte

//go:embed install.sh
var installData []byte

//go:embed CNAME
var cnameData []byte

var style template.CSS
var script []template.JS

var page = template.Must(template.New("main").Parse(data))
var minifier *minify.M

func init() {
	minifier = minify.New()
	minifier.AddFunc("text/css", css.Minify)
	minifier.AddFunc("text/html", html.Minify)
	minifier.AddFunc("text/js", js.Minify)
	o, err := minifier.Bytes("text/css", styleData)
	if err != nil {
		panic(err)
	}
	style = template.CSS(o)
	o, err = minifier.Bytes("text/js", scriptData)
	if err != nil {
		panic(err)
	}
	script = append(script, template.JS(o))
}

const Icon = `href="data:image/svg+xml;base64,"`
const LOGO = `src="data:image/svg+xml;base64,"`

func main() {
	flag.Parse()
	src := flag.Arg(0)
	out := flag.Arg(1)
	err := build(src, out)
	if err != nil {
		fail(err)
	}
}

func build(src, dst string) error {
	os.MkdirAll(dst, 0755)
	err := writeDocs(src, dst)
	if err != nil {
		return err
	}

	return errors.Join(
		writeDocs(src, dst),
		writeBlog(src, dst),
		copyFiles(dst),
	)
}

func copyFiles(dst string) error {
	return errors.Join(
		os.WriteFile(filepath.Join(dst, "install.sh"), installData, 0500),
		os.WriteFile(filepath.Join(dst, "CNAME"), cnameData, 0500),
	)
}
